
public class demobolean {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean value = true;
		System.out.println(value);
		int a = 3, b = 5;
		boolean value1 = a != b;
		boolean value2 = a > b;
		System.out.println(value1);
		System.out.println(value2);
		System.out.println(value1||value2);
		System.out.println(value1&&value2);
		
		
		
		// 
		
		
	}

}
